function i(e){if(!e)return"-";const t=new Date(e);return Number.isNaN(t.getTime())?"-":`${t.getFullYear()}-${String(t.getMonth()+1).padStart(2,"0")}-${String(t.getDate()).padStart(2,"0")} ${String(t.getHours()).padStart(2,"0")}:${String(t.getMinutes()).padStart(2,"0")}`}function f(e){if(!e)return"-";const t=new Date(e),r=Date.now()-t.getTime(),n=60*1e3,o=60*n,a=24*o;return r<n?"刚刚":r<o?`${Math.floor(r/n)} 分钟前`:r<a?`${Math.floor(r/o)} 小时前`:r<a*7?`${Math.floor(r/a)} 天前`:i(e)}export{i as a,f};
